public class EvenOrOdd {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		if(num%2==0) {
			System.out.println("Even number");
		}else {
				System.out.println("Odd number");
			}
		

	}

}
